              "content": [
                {
                  "type": "paragraph",
                  "attrs": {
                    "textAlign": null
                  },
                  "content": [
                    {
                      "type": "text",
                      "text": "Соблюдение требований по защите персональных данных водителей и коммерческой тайны."
                    }
                  ]
                }
              ]
